sqlMap.statement:


/*A*/ SELECT SUBSTR (A.YMGT_JOB_ID, 1, 11) AS YMS_JOB_PGM_ID /* 수익관리작업PGM     */
/*A*/       ,A.YMGT_PROC_DV_ID 		/* 수익관리프로세스구분ID*/
/*A*/       ,C.YMGT_PGM_NM 			/* 수익관리프로그램명  */
/*A*/       ,A.JOB_PRS_CNT 			/* 처리건수            */
/*A*/       ,A.JOB_NML_CNT 			/* 정상건수            */
/*A*/       ,A.JOB_ERR_CNT 			/* 에러건수            */
/*A*/       ,A.JOB_CFM_TGT_CNT 		/* 확인대상건수        */
/*A*/       ,A.YMGT_NML_CLS_FLG 		/* 정상종료여부        */
/*A*/       ,CASE
/*A*/            WHEN A.YMGT_NML_CLS_FLG = 'N'
/*A*/             AND A.JOB_CLS_DTTM IS NULL
/*A*/            THEN
/*A*/                '처리중'
/*A*/            WHEN A.YMGT_NML_CLS_FLG = 'N'
/*A*/             AND A.JOB_CLS_DTTM IS NOT NULL
/*A*/            THEN
/*A*/                '처리오류'
/*A*/            WHEN A.YMGT_NML_CLS_FLG = 'Y'
/*A*/             AND A.JOB_CLS_DTTM IS NOT NULL
/*A*/            THEN
/*A*/                '처리완료'
/*A*/            ELSE
/*A*/                ' '
/*A*/        END
/*A*/            AS JOB_PROG_STAT 				  /* 작업진행상태        */
/*A*/       ,NVL (A.JOB_DTTM, ' ') AS PROC_JOB_DTTM /* 프로세스작업일시    */
/*A*/       ,NVL (A.JOB_CLS_DTTM, ' ') AS PROC_JOB_CLS_DTTM /* 프로세스작업종료일시*/
/*A*/   FROM TB_YYFD010 A, TB_YYFB013 C
/*A*/  WHERE A.YMGT_JOB_ID LIKE 'E_Y_%'
/*A*/    AND SUBSTR (A.JOB_DTTM, 1, 8) = '20150331'
/*A*/    AND A.YMGT_PROC_DV_ID = C.YMGT_PGM_ID
/*A*/ 
/*A*/ 
/*A*/
