mapper.select:
SELECT  CODE_SQ, MESSAGE_VALUE, COUNTRY_CD
FROM TB_FR_CODE_MULTI_LANG_INFO
WHERE 1=1

	AND CODE_SQ = #{codeSq}


	AND COUNTRY_CD = #{countryCd}

mapper.update:
UPDATE TB_FR_CODE_MULTI_LANG_INFO SET
MESSAGE_VALUE = #{messageValue} ,
UPDATE_USR_ID = #{updateUsrId}, UPDATE_DT = NOW() WHERE CODE_SQ = #{codeSq}
AND COUNTRY_CD = #{countryCd}
