mapper.select:
SELECT * FROM (SELECT @RNUM := @RNUM + 1 AS NUM, ROW.* FROM (
SELECT MESSAGE_KEY, LARGE_CATEGORY, MEDIUM_CATEGORY, SMALL_CATEGORY, MESSAGE_VALUE, CREATE_USR_ID, CREATE_DT FROM TB_FR_MESSAGE_INFO WHERE 1=1
AND UPPER(MESSAGE_KEY) LIKE CONCAT('%', UPPER(#{srchMessageKey}),'%')
AND UPPER(MESSAGE_VALUE) LIKE CONCAT('%', UPPER(#{srchMessageValue}),'%')
AND LARGE_CATEGORY = #{srchLargeCategoryCd}
AND MEDIUM_CATEGORY = #{srchMediumCategoryCd}
AND SMALL_CATEGORY = #{srchSmallCategoryCd}
ORDER BY MESSAGE_KEY
SELECT INFO.MESSAGE_KEY, INFO.LARGE_CATEGORY, INFO.MEDIUM_CATEGORY, INFO.SMALL_CATEGORY, MULTI.MESSAGE_VALUE, MULTI.CREATE_USR_ID, MULTI.CREATE_DT FROM TB_FR_MESSAGE_INFO INFO, TB_FR_MULTI_MESSAGE_INFO MULTI WHERE 1=1 AND INFO.MESSAGE_KEY = MULTI.MESSAGE_KEY AND MULTI.COUNTRY_CD = #{country}
AND UPPER(INFO.MESSAGE_KEY) LIKE CONCAT('%', UPPER(#{srchMessageKey}),'%')
AND UPPER(INFO.MESSAGE_VALUE) LIKE CONCAT('%', UPPER(#{srchMessageValue}),'%')
AND INFO.LARGE_CATEGORY = #{srchLargeCategoryCd}
AND INFO.MEDIUM_CATEGORY = #{srchMediumCategoryCd}
AND INFO.SMALL_CATEGORY = #{srchSmallCategoryCd}
ORDER BY INFO.MESSAGE_KEY
) ROW, ( SELECT @RNUM := 0 ) B ) F WHERE NUM <= #{readcount} AND NUM > #{readindex}

SELECT MESSAGE_KEY, MESSAGE_VALUE, COUNTRY_CD
FROM
TB_FR_MULTI_MESSAGE_INFO
where 1=1

	AND COUNTRY_CD = #{country}


	AND MESSAGE_KEY = #{messageKey}

SELECT COUNT(*)
FROM TB_FR_MESSAGE_INFO
WHERE 1=1

	AND UPPER(TB_FR_MESSAGE_INFO.MESSAGE_KEY) LIKE CONCAT('%',UPPER(#{srchMessageKey}),'%')


	AND UPPER(TB_FR_MESSAGE_INFO.MESSAGE_VALUE) LIKE CONCAT('%',UPPER(#{srchMessageValue}),'%')


	AND LARGE_CATEGORY = #{srchLargeCategoryCd}


	AND MEDIUM_CATEGORY = #{srchMediumCategoryCd}


	AND SMALL_CATEGORY = #{srchSmallCategoryCd}

mapper.update:
UPDATE TB_FR_MULTI_MESSAGE_INFO SET

	MESSAGE_VALUE = #{messageValue},

UPDATE_DT = NOW()
WHERE MESSAGE_KEY = #{messageKey}
	AND COUNTRY_CD = #{countryCd}
