mapper.select:
SELECT  MENU_SQ, MESSAGE_VALUE, COUNTRY_CD
FROM TB_FR_MENU_MULTI_LANG_INFO
WHERE 1=1

	AND MENU_SQ = #{menuSq}


	AND COUNTRY_CD = #{countryCd}

mapper.update:
UPDATE TB_FR_MENU_MULTI_LANG_INFO SET
MESSAGE_VALUE = #{messageValue} ,
UPDATE_USR_ID = #{updateUsrId}, UPDATE_DT = systimestamp WHERE MENU_SQ = #{menuSq}
AND COUNTRY_CD = #{countryCd}
